        "source_site": "tsa",
        "listing_url": "https://www.tsabusinessschool.co.za/2025/10/08/unlock-team-potential-with-strengths-based-leadership",
        "slug": "unlock-team-potential-with-strengths-based-leadership",
        "title": "Strengths-Based Leadership: Leveraging Individual Strengths to Create Dynamic Teams",
        "excerpt": "",
        "published_at": "",
